import React from 'react';
import styled from 'styled-components';

// Define the styled components
const AboutPage = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
  background-color: #f4f4f9;
`;

const AboutInfo = styled.div`
  max-width: 600px;
  margin: 0 auto;
  background-color: #fff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const Heading = styled.h1`
  text-align: center;
  color: #333;
`;

const Paragraph = styled.p`
  font-size: 16px;
  line-height: 1.6;
  color: #666;
`;

const List = styled.ul`
  font-size: 16px;
  line-height: 1.6;
  color: #666;
`;

const About = () => {
  return (
    <AboutPage>
      <Heading>About Us</Heading>
      <Paragraph>
        Welcome to [Your Grocery Web App Name]. We are committed to providing
        high-quality groceries delivered to your doorsteps at competitive prices.
        Our team works hard to ensure that you have a seamless shopping experience,
        from browsing our wide selection of products to getting your order delivered quickly and efficiently.
      </Paragraph>
      
      <AboutInfo>
        <h3>Our Mission</h3>
        <Paragraph>
          At [Your App Name], we believe in making grocery shopping easier and more convenient for everyone. Our
          mission is to provide customers with a hassle-free way to purchase fresh groceries online and have them
          delivered directly to their homes. Whether you're shopping for everyday essentials or unique ingredients,
          we've got you covered.
        </Paragraph>

        <h3>Our Values</h3>
        <List>
          <li>Quality Products</li>
          <li>Customer Satisfaction</li>
          <li>Fast and Reliable Delivery</li>
          <li>Affordable Prices</li>
        </List>

        <h3>Our Team</h3>
        <Paragraph>
          Our team consists of dedicated individuals who strive to bring the best service to our customers. From our
          support team to our delivery drivers, each member of our team plays a key role in ensuring that your
          shopping experience is a positive one.
        </Paragraph>
      </AboutInfo>
    </AboutPage>
  );
};

export default About;
