import React from 'react';

const About = () => {
  // Define styles in a JS object
  const aboutPageStyle = {
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    backgroundColor: '#f4f4f9',
  };

  const aboutInfoStyle = {
    maxWidth: '600px',
    margin: '0 auto',
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  };

  const headingStyle = {
    textAlign: 'center',
    color: '#333',
  };

  const paragraphStyle = {
    fontSize: '16px',
    lineHeight: '1.6',
    color: '#666',
  };

  return (
    <div style={aboutPageStyle}>
      <h1 style={headingStyle}>About Us</h1>
      <p style={paragraphStyle}>
        Welcome to amit . We are committed to providing
        high-quality groceries delivered to your doorsteps at competitive prices.
        Our team works hard to ensure that you have a seamless shopping experience,
        from browsing our wide selection of products to getting your order delivered quickly and efficiently.
      </p>
      
      <div style={aboutInfoStyle}>
        <h3>Our Mission</h3>
        <p style={paragraphStyle}>
          At amit , we believe in making grocery shopping easier and more convenient for everyone. Our
          mission is to provide customers with a hassle-free way to purchase fresh groceries online and have them
          delivered directly to their homes. Whether you're shopping for everyday essentials or unique ingredients,
          we've got you covered.
        </p>

        <h3>Our Values</h3>
        <ul style={paragraphStyle}>
          <li>Quality Products</li>
          <li>Customer Satisfaction</li>
          <li>Fast and Reliable Delivery</li>
          <li>Affordable Prices</li>
        </ul>

        <h3>Our Team</h3>
        <p style={paragraphStyle}>
          Our team consists of dedicated individuals who strive to bring the best service to our customers. From our
          support team to our delivery drivers, each member of our team plays a key role in ensuring that your
          shopping experience is a positive one.
        </p>
      </div>
    </div>
  );
};

export default About;
