import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import AllProduct from "../component/AllProduct";
import { addCartItem } from "../redux/productSlide";

const Menu = () => {
  const { filterby } = useParams(); // Get product ID from URL
  const navigate = useNavigate(); // Hook for navigation
  const dispatch = useDispatch(); // Hook for dispatching Redux actions
  const productData = useSelector((state) => state.product.productList); // Get product list from Redux store

  // Check if product data is available
  if (!productData || productData.length === 0) {
    return <div>Loading...</div>; // Show loading message if data is not yet available
  }

  // Find the product matching the filterby ID
  const productDisplay = productData.find((el) => String(el._id) === String(filterby));

  // Handle case where no product matches the ID
  if (!productDisplay) {
    return (
      <div className="text-center">
        <h2 className="text-red-500 text-2xl">Product not found</h2>
        <p>Check the URL or return to the homepage.</p>
      </div>
    );
  }

  // Add the product to the cart
  const handleAddCartProduct = () => {
    dispatch(addCartItem(productDisplay));
  };

  // Add the product to the cart and navigate to the cart page
  const handleBuy = () => {
    dispatch(addCartItem(productDisplay));
    navigate("/cart");
  };

  return (
    <div className="p-2 md:p-4">
      <div className="w-full max-w-4xl m-auto md:flex bg-white">
        {/* Product image */}
        <div className="max-w-sm overflow-hidden w-full p-5">
          <img
            src={productDisplay.image}
            alt={productDisplay.name}
            className="hover:scale-105 transition-all h-full"
          />
        </div>

        {/* Product details */}
        <div className="flex flex-col gap-1">
          <h3 className="font-semibold text-slate-600 capitalize text-2xl md:text-4xl">
            {productDisplay.name}
          </h3>
          <p className="text-slate-500 font-medium text-2xl">{productDisplay.category}</p>
          <p className="font-bold md:text-2xl">
            <span className="text-red-500">â‚¹</span>
            <span>{productDisplay.price}</span>
          </p>

          {/* Buttons for Buy and Add to Cart */}
          <div className="flex gap-3">
            <button
              onClick={handleBuy}
              className="bg-yellow-500 py-1 mt-2 rounded hover:bg-yellow-600 min-w-[100px]"
            >
              Buy
            </button>
            <button
              onClick={handleAddCartProduct}
              className="bg-yellow-500 py-1 mt-2 rounded hover:bg-yellow-600 min-w-[100px]"
            >
              Add Cart
            </button>
          </div>

          {/* Product description */}
          <div>
            <p className="text-slate-600 font-medium">Description :</p>
            <p>{productDisplay.description}</p>
          </div>
        </div>
      </div>

      {/* Related products section */}
      <AllProduct heading={"Related Product"} />
    </div>
  );
};

export default Menu;
