import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import AllProduct from "../component/AllProduct";
import { addCartItem } from "../redux/productSlide";

const Menu = () => {
  const { filterby } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const productData = useSelector((state) => state.product.productList);

  // Find the product by filterby parameter
  const productDisplay = productData.find((el) => el._id === filterby);

  // If no product is found, show an error or fallback UI
  if (!productDisplay) {
    return (
      <div className="flex items-center justify-center h-screen text-red-600 text-xl font-semibold">
        Product not found.
      </div>
    );
  }

  const handleAddCartProduct = () => {
    dispatch(addCartItem(productDisplay));
  };

  const handleBuy = () => {
    dispatch(addCartItem(productDisplay));
    navigate("/cart");
  };

  return (
    <div className="p-4 md:p-6 bg-gray-50 min-h-screen">
      {/* Product Details Section */}
      <div className="w-full max-w-7xl mx-auto md:flex bg-white shadow-lg rounded-lg overflow-hidden">
        {/* Product Image Section */}
        <div className="w-full md:w-1/2 p-6 flex justify-center items-center">
          <img
            src={productDisplay.image || "fallback-image.jpg"} // Fallback image
            alt={productDisplay.name}
            className="hover:scale-105 transition-transform duration-300 ease-in-out rounded-lg w-full h-auto object-contain max-h-[400px]"
          />
        </div>

        {/* Product Info Section */}
        <div className="flex flex-col gap-6 p-6 md:w-1/2">
          <h3 className="font-bold text-gray-800 capitalize text-3xl md:text-4xl">
            {productDisplay.name}
          </h3>
          <p className="text-gray-600 text-lg md:text-xl">{productDisplay.category}</p>
          <p className="text-gray-800 text-2xl md:text-3xl font-semibold">
            <span className="text-green-600">₹</span>
            {productDisplay.price}
          </p>

          {/* Buttons */}
          <div className="flex gap-4">
            <button
              onClick={handleBuy}
              className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-6 rounded-lg shadow-md transition duration-300"
            >
              Buy Now
            </button>
            <button
              onClick={handleAddCartProduct}
              className="bg-yellow-500 hover:bg-yellow-600 text-white font-semibold py-2 px-6 rounded-lg shadow-md transition duration-300"
            >
              Add to Cart
            </button>
          </div>

          {/* Product Description */}
          <div>
            <h4 className="text-gray-700 font-semibold text-lg">Description:</h4>
            <p className="text-gray-600 leading-relaxed">{productDisplay.description}</p>
          </div>
        </div>
      </div>

      {/* Menu Items Section */}
      <div className="mt-12">
        <h2 className="text-2xl md:text-3xl font-bold text-gray-800 text-center mb-6">
          Explore Our Menu
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
          {productData.map((item) => (
            <div
              key={item._id}
              className="bg-white rounded-lg shadow-lg p-4 hover:shadow-xl transition duration-300"
            >
              <img
                src={item.image || "fallback-image.jpg"}
                alt={item.name}
                className="w-full h-40 object-cover rounded-lg mb-4"
              />
              <h3 className="font-bold text-gray-800 text-lg">{item.name}</h3>
              <p className="text-gray-600 text-sm">{item.category}</p>
              <p className="font-bold text-gray-800 text-lg mt-2">
                ₹{item.price}
              </p>
              <button
                onClick={() => dispatch(addCartItem(item))}
                className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 mt-4 rounded-lg w-full transition duration-300"
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Menu;
