import React from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const Menu = () => {
  const { filterby } = useParams();
  const productData = useSelector((state) => state.product.productList);

  if (!productData || productData.length === 0) {
    return <div>Loading...</div>;
  }

  const productDisplay = productData.find((el) => el._id === filterby);

  if (!productDisplay) {
    return (
      <div className="text-center">
        <h2 className="text-red-500 text-2xl">Product not found</h2>
        <p>Check the URL or return to the homepage.</p>
      </div>
    );
  }

  return (
    <div>
      <div className="w-full max-w-3xl bg-slate-400 m-auto">
        <div>
          <img src={productDisplay.image} alt={productDisplay.name} />
        </div>
        <div>
          <h3>{productDisplay.name}</h3>
          <p>{productDisplay.description}</p>
        </div>
      </div>
    </div>
  );
};

export default Menu;
